import React, { useState, useEffect } from 'react';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './styles.css';

function App() {
  const [currentPage, setCurrentPage] = useState('login');
  const [currentUser, setCurrentUser] = useState(null);
  const [tasks, setTasks] = useState([]);
  const [currentFilter, setCurrentFilter] = useState('all');
  const [currentTheme, setCurrentTheme] = useState(localStorage.getItem('theme') || 'light');

  // Load data from localStorage on component mount
  useEffect(() => {
    const savedUser = localStorage.getItem('currentUser');
    const savedTasks = localStorage.getItem('tasks');
    
    if (savedUser) {
      setCurrentUser(JSON.parse(savedUser));
      setCurrentPage('task');
    }
    
    if (savedTasks) {
      setTasks(JSON.parse(savedTasks));
    }
    
    applyTheme(currentTheme);
  }, []);

  const applyTheme = (theme) => {
    document.documentElement.setAttribute('data-theme', theme);
  };

  const toggleTheme = () => {
    const newTheme = currentTheme === 'light' ? 'dark' : 'light';
    setCurrentTheme(newTheme);
    localStorage.setItem('theme', newTheme);
    applyTheme(newTheme);
  };

  const showMessage = (message, type = 'success') => {
    if (type === 'success') {
      toast.success(message);
    } else {
      toast.error(message);
    }
  };

  // Auth functions
  const login = (username, password) => {
    const users = JSON.parse(localStorage.getItem('users')) || [];
    const user = users.find(u => u.username === username && u.password === password);
    
    if (user) {
      setCurrentUser(user);
      localStorage.setItem('currentUser', JSON.stringify(user));
      setCurrentPage('task');
      showMessage('Welcome back!');
    } else {
      showMessage('Invalid username or password!', 'error');
    }
  };

  const register = (username, password, confirmPassword) => {
    if (password !== confirmPassword) {
      showMessage('Passwords do not match!', 'error');
      return false;
    }

    const users = JSON.parse(localStorage.getItem('users')) || [];
    if (users.find(u => u.username === username)) {
      showMessage('Username already exists!', 'error');
      return false;
    }

    const newUser = {
      id: Date.now(),
      username: username,
      password: password
    };

    users.push(newUser);
    localStorage.setItem('users', JSON.stringify(users));
    showMessage('Registration successful! Please login.');
    setCurrentPage('login');
    return true;
  };

  const logout = () => {
    setCurrentUser(null);
    localStorage.removeItem('currentUser');
    setCurrentPage('login');
  };

  // Task functions
  const addTask = (title, deadline) => {
    if (!title.trim()) return;

    const newTask = {
      id: Date.now(),
      title: title.trim(),
      deadline: deadline,
      completed: false,
      createdAt: new Date().toISOString(),
      priority: 'medium',
      userId: currentUser.id
    };

    const updatedTasks = [...tasks, newTask];
    setTasks(updatedTasks);
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));
    showMessage('Task added successfully!');
  };

  const toggleTaskComplete = (taskId) => {
    const updatedTasks = tasks.map(task => 
      task.id === taskId ? { ...task, completed: !task.completed } : task
    );
    setTasks(updatedTasks);
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));
    showMessage('Task status updated!');
  };

  const editTask = (taskId, newTitle, newDeadline, newPriority) => {
    const updatedTasks = tasks.map(task =>
      task.id === taskId ? { ...task, title: newTitle, deadline: newDeadline, priority: newPriority } : task
    );
    setTasks(updatedTasks);
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));
    showMessage('Task updated successfully!');
  };

  const deleteTask = (taskId) => {
    const updatedTasks = tasks.filter(task => task.id !== taskId);
    setTasks(updatedTasks);
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));
    showMessage('Task deleted successfully!');
  };

  const filterTasks = (type) => {
    setCurrentFilter(type);
  };

  const getFilteredTasks = () => {
    const userTasks = tasks.filter(task => task.userId === currentUser?.id);
    return userTasks.filter(task => {
      if (currentFilter === 'pending') return !task.completed;
      if (currentFilter === 'completed') return task.completed;
      return true;
    });
  };

  const getProgress = () => {
    const userTasks = tasks.filter(task => task.userId === currentUser?.id);
    const totalTasks = userTasks.length;
    const completedTasks = userTasks.filter(task => task.completed).length;
    return totalTasks > 0 ? Math.round((completedTasks / totalTasks) * 100) : 0;
  };

  return (
    <div className="App">
      <ToastContainer position="top-right" autoClose={3000} />
      
      {/* Login Page */}
      {currentPage === 'login' && (
        <LoginPage 
          onLogin={login}
          onShowRegister={() => setCurrentPage('register')}
        />
      )}
      
      {/* Register Page */}
      {currentPage === 'register' && (
        <RegisterPage 
          onRegister={register}
          onShowLogin={() => setCurrentPage('login')}
        />
      )}
      
      {/* Task Page */}
      {currentPage === 'task' && currentUser && (
        <TaskPage 
          tasks={getFilteredTasks()}
          progress={getProgress()}
          currentFilter={currentFilter}
          currentTheme={currentTheme}
          onAddTask={addTask}
          onToggleTaskComplete={toggleTaskComplete}
          onEditTask={editTask}
          onDeleteTask={deleteTask}
          onFilterTasks={filterTasks}
          onToggleTheme={toggleTheme}
          onLogout={logout}
          currentUser={currentUser}
        />
      )}
    </div>
  );
}

// Login Component
function LoginPage({ onLogin, onShowRegister }) {
  const [formData, setFormData] = useState({ username: '', password: '' });

  const handleSubmit = (e) => {
    e.preventDefault();
    onLogin(formData.username, formData.password);
  };

  return (
    <div id="loginPage" className="page active">
      <div className="auth-container">
        <div className="auth-form">
          <h2>Login to Task Manager</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="username">Username</label>
              <input 
                type="text" 
                id="username" 
                value={formData.username}
                onChange={(e) => setFormData({...formData, username: e.target.value})}
                required 
                placeholder="Enter your username" 
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input 
                type="password" 
                id="password" 
                value={formData.password}
                onChange={(e) => setFormData({...formData, password: e.target.value})}
                required 
                placeholder="Enter your password" 
              />
            </div>
            <button type="submit" className="btn btn-primary">Login</button>
          </form>
          <div className="auth-switch">
            Don't have an account? <a href="#" onClick={onShowRegister}>Register here</a>
          </div>
        </div>
      </div>
    </div>
  );
}

// Register Component
function RegisterPage({ onRegister, onShowLogin }) {
  const [formData, setFormData] = useState({ 
    username: '', 
    password: '', 
    confirmPassword: '' 
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (onRegister(formData.username, formData.password, formData.confirmPassword)) {
      setFormData({ username: '', password: '', confirmPassword: '' });
    }
  };

  return (
    <div id="registerPage" className="page">
      <div className="auth-container">
        <div className="auth-form">
          <h2>Create Account</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="regUsername">Username</label>
              <input 
                type="text" 
                id="regUsername" 
                value={formData.username}
                onChange={(e) => setFormData({...formData, username: e.target.value})}
                required 
                placeholder="Choose a username" 
              />
            </div>
            <div className="form-group">
              <label htmlFor="regPassword">Password</label>
              <input 
                type="password" 
                id="regPassword" 
                value={formData.password}
                onChange={(e) => setFormData({...formData, password: e.target.value})}
                required 
                placeholder="Create a password" 
              />
            </div>
            <div className="form-group">
              <label htmlFor="confirmPassword">Confirm Password</label>
              <input 
                type="password" 
                id="confirmPassword" 
                value={formData.confirmPassword}
                onChange={(e) => setFormData({...formData, confirmPassword: e.target.value})}
                required 
                placeholder="Confirm your password" 
              />
            </div>
            <button type="submit" className="btn btn-primary">Register</button>
          </form>
          <div className="auth-switch">
            Already have an account? <a href="#" onClick={onShowLogin}>Login here</a>
          </div>
        </div>
      </div>
    </div>
  );
}

// Task Page Component
function TaskPage({ 
  tasks, 
  progress, 
  currentFilter, 
  currentTheme, 
  onAddTask, 
  onToggleTaskComplete, 
  onEditTask, 
  onDeleteTask, 
  onFilterTasks, 
  onToggleTheme, 
  onLogout, 
  currentUser 
}) {
  const [taskForm, setTaskForm] = useState({ title: '', deadline: '' });

  const handleAddTask = (e) => {
    e.preventDefault();
    onAddTask(taskForm.title, taskForm.deadline);
    setTaskForm({ title: '', deadline: '' });
  };

  const handleEditTask = (task) => {
    const newTitle = prompt('Edit task title:', task.title);
    if (newTitle && newTitle.trim()) {
      const newDeadline = prompt('Edit deadline (YYYY-MM-DDTHH:MM):', task.deadline || '');
      const newPriority = prompt('Edit priority (low/medium/high):', task.priority);
      onEditTask(task.id, newTitle.trim(), newDeadline, newPriority);
    }
  };

  return (
    <div id="taskPage" className="page">
      <div className="container">
        <div className="header">
          <h1>My Task Manager</h1>
          <div className="header-controls">
            <button onClick={onToggleTheme} className="btn btn-secondary">
              {currentTheme === 'dark' ? '☀️ Light Mode' : '🌙 Dark Mode'}
            </button>
            <button onClick={onLogout} className="btn btn-secondary">Logout</button>
          </div>
        </div>

        <div className="progress-container">
          <label>Progress:</label>
          <div className="progress-bar">
            <div className="progress-fill" style={{ width: `${progress}%` }}></div>
          </div>
          <span id="progressText">{progress}%</span>
        </div>

        <div className="filters">
          <button 
            onClick={() => onFilterTasks('all')} 
            className={currentFilter === 'all' ? 'active' : ''}
          >
            All Tasks
          </button>
          <button 
            onClick={() => onFilterTasks('pending')} 
            className={currentFilter === 'pending' ? 'active' : ''}
          >
            Pending
          </button>
          <button 
            onClick={() => onFilterTasks('completed')} 
            className={currentFilter === 'completed' ? 'active' : ''}
          >
            Completed
          </button>
        </div>

        <form onSubmit={handleAddTask} id="taskForm">
          <input 
            type="text" 
            id="taskTitle" 
            value={taskForm.title}
            onChange={(e) => setTaskForm({...taskForm, title: e.target.value})}
            placeholder="Enter task title" 
            required 
          />
          <input 
            type="datetime-local" 
            id="taskDeadline" 
            value={taskForm.deadline}
            onChange={(e) => setTaskForm({...taskForm, deadline: e.target.value})}
          />
          <button type="submit">Add Task</button>
        </form>

        <TaskList 
          tasks={tasks}
          onToggleComplete={onToggleTaskComplete}
          onEdit={handleEditTask}
          onDelete={onDeleteTask}
        />
      </div>
    </div>
  );
}

// Task List Component
function TaskList({ tasks, onToggleComplete, onEdit, onDelete }) {
  if (tasks.length === 0) {
    return (
      <ul id="taskList">
        <li className="empty-state">No tasks found. Add your first task above!</li>
      </ul>
    );
  }

  return (
    <ul id="taskList">
      {tasks.map(task => (
        <TaskItem 
          key={task.id}
          task={task}
          onToggleComplete={onToggleComplete}
          onEdit={onEdit}
          onDelete={onDelete}
        />
      ))}
    </ul>
  );
}

// Task Item Component
function TaskItem({ task, onToggleComplete, onEdit, onDelete }) {
  const isOverdue = !task.completed && task.deadline && new Date(task.deadline) < new Date();
  
  return (
    <li className={`${task.completed ? 'completed' : ''} priority-${task.priority} ${isOverdue ? 'overdue' : ''}`}>
      <div className="task-info">
        <strong>{task.title}</strong>
        <div className="task-meta">
          <small>
            {task.deadline 
              ? `Deadline: ${new Date(task.deadline).toLocaleString()}`
              : 'No deadline set'
            }
          </small>
          <span className={`priority-badge priority-${task.priority}-badge`}>
            {task.priority.charAt(0).toUpperCase() + task.priority.slice(1)} Priority
          </span>
          <span className={`status-badge status-${task.completed ? 'completed' : 'pending'}`}>
            {task.completed ? 'Completed' : 'Pending'}
          </span>
        </div>
      </div>
      <div className="task-actions">
        <button 
          className="complete-btn"
          onClick={() => onToggleComplete(task.id)}
        >
          {task.completed ? 'Undo' : 'Complete'}
        </button>
        <button 
          className="edit-btn"
          onClick={() => onEdit(task)}
        >
          Edit
        </button>
        <button 
          className="delete-btn"
          onClick={() => onDelete(task.id)}
        >
          Delete
        </button>
      </div>
    </li>
  );
}

export default App;
